mapper.select:
SELECT MEMBER_SEQ
FROM SM_MEMBER
WHERE MEMBER_EMAIL = #{MEMBER_EMAIL}

SELECT COUNT(MEMBER_SEQ) as COUNT
FROM SM_SURVEY_RESPONSE
WHERE MEMBER_SEQ =#{MEMBER_SEQ} and SURVEY_SEQ = #{SURVEY_SEQ}

mapper.update:
UPDATE SM_SURVEY
SET
SURVEY_NAME = #{SURVEY_NAME}
WHERE
SURVEY_SEQ = #{SURVEY_SEQ}

UPDATE SM_QUESTION
SET
QUESTION_NAME = #{QUESTION_NAME}
WHERE
QUESTION_SEQ = #{QUESTION_SEQ}

UPDATE SM_QUESTIONRVIEW
SET
VIEW_NAME = #{VIEW_NAME}
WHERE
VIEW_SEQ = #{VIEW_SEQ}

UPDATE SM_SURVEY_RESPONSE
SET
OBJECTIVE_RESPONSE = #{OBJECTIVE_RESPONSE}
WHERE MEMBER_SEQ = #{MEMBER_SEQ} and QUESTION_SEQ = #{QUESTION_SEQ} and SURVEY_SEQ = #{SURVEY_SEQ}
